// /client/App.js
import React, { Component } from "react";
import viewDetail from './viewDetail';
import Home from './Home';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";


class App extends Component {

  // initialize our state 
  state = {
    data: [],
    id: null,
    brand: null,
    model: null,
    color: null,
    fuel_type: null,
    engine_volume: null,
    traction: null,
    price: null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null,
    selected: [],
    newselected: [],
  };

  givemestate(id, brand, model, color, fuel_type, engine_volume, traction, price) {
    console.log("APP: "+id);
    this.setState({id:id});
    this.setState({brand:brand});
    this.setState({model:model});
    this.setState({color:color});
    this.setState({fuel_type:fuel_type});
    this.setState({engine_volume:engine_volume});
    this.setState({traction:traction});
    this.setState({price:price});
  }

  render() {
    return (<Router>
      <div>
        <Route exact path="/"           render={(props) => <Home returnstate={this.givemestate.bind(this)} />} />
        <Route exact path="/viewdetail" render={(props) => <viewDetail detailinfo={this.state.id} />}  />
      </div>
    </Router >
    )
  }
}

ReactDOM.render(<App />, document.querySelector('#root'));


export default App;
