// /client/App.js
import React, { Component } from "react";
import axios from "axios";

class App extends Component {
  // initialize our state 
  state = {
    data: [],
    id: 0,
    brand: null,
    model: null,
    color: null,
    fuel_type: null,
    engine_volume: null,
    traction: null,
    price: null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null
  };

  // when component mounts, first thing it does is fetch all existing data in our db
  // then we incorporate a polling logic so that we can easily see if our db has 
  // changed and implement those changes into our UI
  componentDidMount() {
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  // never let a process live forever 
  // always kill a process everytime we are done using it
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }

  // just a note, here, in the front end, we use the id key of our data object 
  // in order to identify which we want to Update or delete.
  // for our back end, we use the object id assigned by MongoDB to modify 
  // data base entries

  // our first get method that uses our backend api to 
  // fetch data from our data base
  getDataFromDb = () => {
    fetch("http://localhost:3001/api/getData")
      .then(data => data.json())
      .then(res => this.setState({ data: res.data }));
  };

  // our put method that uses our backend api
  // to create new query into our data base
  putDataToDB = message => {


    axios.post("http://localhost:3001/api/putData", {
      id: idToBeAdded,
      brand: brand,
      model: model,
      color: color,
      fuel_type: fuel_type,
      engine_volume: engine_volume,
      traction: traction,
      price: price
    });
  };


  // our delete method that uses our backend api 
  // to remove existing database information
  deleteFromDB = idTodelete => {
    let objIdToDelete = null;
    this.state.data.forEach(dat => {
      if (dat.id === idTodelete) {
        objIdToDelete = dat._id;
      }
    });

    axios.delete("http://localhost:3001/api/deleteData", {
      data: {
        id: objIdToDelete
      }
    });
  };


  // our update method that uses our backend api
  // to overwrite existing data base information
  updateDB = (idToUpdate, updateToApply) => {
    let objIdToUpdate = null;
    this.state.data.forEach(dat => {
      if (dat.id === idToUpdate) {
        objIdToUpdate = dat._id;
      }
    });

    axios.post("http://localhost:3001/api/updateData", {
      id: objIdToUpdate,
      update: {
        brand: brand,
        model: model,
        color: color,
        fuel_type: fuel_type,
        engine_volume: engine_volume,
        traction: traction,
        price: price
      }
    });
  };


  // here is our UI
  // it is easy to understand their functions when you 
  // see them render into our screen
  render() {
    const { data } = this.state;
    return (
      <div>
        <ul>
          <div>HOLA AMIGO</div>
          <div>{data.length}</div>

          {data.length <= 0
            ? "NO DB ENTRIES YET"
            : data.map(dat => (
              <li style={{ padding: "10px" }} key={data.price}>
                <span style={{ color: "gray" }}> id: </span> {dat.id} <br />
                <span style={{ color: "gray" }}> brand: </span> {dat.brand} <br />
                <span style={{ color: "gray" }}> model: </span> {dat.model} <br />
                <span style={{ color: "gray" }}> color: </span> {dat.color} <br />
                <span style={{ color: "gray" }}> fuel_type: </span> {dat.fuel_type} <br />
                <span style={{ color: "gray" }}> engine_volume: </span> {dat.engine_volume} <br />
                <span style={{ color: "gray" }}> traction: </span> {dat.traction} <br />
                <span style={{ color: "gray" }}> price: </span> {dat.price} <br />
              </li>
            ))}
        </ul>

        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ id: e.target.value })}
            placeholder="ID"
            style={{ width: "200px" }}
          />
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ brand: e.target.value })}
            placeholder="brand"
            style={{ width: "200px" }}
          />
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ model: e.target.value })}
            placeholder="model"
            style={{ width: "200px" }}
          />
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ color: e.target.value })}
            placeholder="color"
            style={{ width: "200px" }}
          />
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ fuel_type: e.target.value })}
            placeholder="fuel_type"
            style={{ width: "200px" }}
          />
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ engine_volume: e.target.value })}
            placeholder="engine_volume"
            style={{ width: "200px" }}
          />
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ traction: e.target.value })}
            placeholder="traction"
            style={{ width: "200px" }}
          />
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ price: e.target.value })}
            placeholder="price"
            style={{ width: "200px" }}
          />
        </div>

        <div><button onClick={() => this.putDataToDB(this.state.message)}>
          ADD
          </button></div>

        <div style={{ padding: "10px" }}>
          <input
            type="text"
            onChange={e => this.setState({ message: e.target.value })}
            placeholder="add something in the database"
            style={{ width: "200px" }}
          />
          <button onClick={() => this.putDataToDB(this.state.message)}>
            ADD
          </button>
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            style={{ width: "200px" }}
            onChange={e => this.setState({ idToDelete: e.target.value })}
            placeholder="put id of item to delete here"
          />
          <button onClick={() => this.deleteFromDB(this.state.idToDelete)}>
            DELETE
          </button>
        </div>
        <div style={{ padding: "10px" }}>
          <input
            type="text"
            style={{ width: "200px" }}
            onChange={e => this.setState({ idToUpdate: e.target.value })}
            placeholder="id of item to update here"
          />
          <input
            type="text"
            style={{ width: "200px" }}
            onChange={e => this.setState({ updateToApply: e.target.value })}
            placeholder="put new value of the item here"
          />
          <button
            onClick={() =>
              this.updateDB(this.state.idToUpdate, this.state.updateToApply)
            }
          >
            UPDATE
          </button>
        </div>
      </div>
    );
  }
}

export default App;
