import React, { Component } from "react";
import PropTypes from 'prop-types';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Chip from '@material-ui/core/Chip';
import FaceIcon from '@material-ui/icons/Face';
import DoneIcon from '@material-ui/icons/Done';


class ViewDetail extends Component {

    createSortHandler = property => event => {
        this.props.onRequestSort(event, property);
    };

    render() {

        //this.givemestate(this.props.id, this.props.brand, this.props.model, this.props.color, this.props.fuel_type, this.props.engine_volume, this.props.traction, this.props.price);

        return (
            <Card style={{ padding: "50px", margin: "50px", border: "15px", borderRadius: "20px", textAlign: "center" }}>
                <h1>Detail View</h1>
                <CardActionArea>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                            {this.props.brand}
                        </Typography>
                        <Typography component="p">
                            <Chip
                                icon={< car-side />}
                                label={this.props.id}
                                clickable
                                color="primary"
                                deleteIcon={<DoneIcon />}
                            />
                            <Chip
                                icon={<format-list-bulleted-type />}
                                label={this.props.model}
                                clickable
                                color="primary"
                                deleteIcon={<DoneIcon />}
                            />
                            <Chip
                                icon={< invert-colors />}
                                label={this.props.color}
                                clickable
                                color="primary"
                                deleteIcon={<DoneIcon />}
                            />
                            <Chip
                                icon={< fuel />}
                                label={this.props.fuel_type}
                                clickable
                                color="primary"
                                deleteIcon={<DoneIcon />}
                            />
                            <Chip
                                icon={< engine />}
                                label={this.props.engine_volume}
                                clickable
                                color="primary"
                                deleteIcon={<DoneIcon />}
                            />
                            <Chip
                                icon={<FaceIcon />}
                                label={this.props.traction}
                                clickable
                                color="primary"
                                deleteIcon={<DoneIcon />}
                            />
                            <Chip
                                icon={<FaceIcon />}
                                label={this.props.price}
                                clickable
                                color="primary"
                                deleteIcon={<DoneIcon />}
                            />
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Link to="/">
                        <Button size="small" color="primary" >
                            <b> Back</b>
                        </Button>
                    </Link>
                    <Button size="small" color="secondary" >
                        <b>Delete this Car</b>
                    </Button>

                </CardActions>
            </Card >
        );
    }
}

export default ViewDetail;
