import React from 'react';
import PropTypes from 'prop-types';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";


class viewDetail extends React.Component {

    // initialize our state 
    state = {
        data: [],
        id: null,
        brand: "Ejemplo",
        model: null,
        color: null,
        fuel_type: null,
        engine_volume: null,
        traction: null,
        price: null,
        intervalIsSet: false,
        idToDelete: null,
        idToUpdate: null,
        objectToUpdate: null,
        selected: [],
        newselected: [],
    };

    a(id, brand, model, color, fuel_type, engine_volume, traction, price) {
        console.log("viewDetail: " + id);
        this.props.sendState(id, brand, model, color, fuel_type, engine_volume, traction, price)
    }

    createSortHandler = property => event => {
        this.props.onRequestSort(event, property);
    };

    render() {
        const data = this.props.info;
        const selected = this.props.selected;
        console.log(this.state.data);
        console.log(this.state.selected);
        return (
            <Card style={{ padding: "50px", margin: "50px", border: "15px", borderRadius: "20px", textAlign: "center" }}>
                <h1>Detail View</h1>
                <CardActionArea>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                           {this.state.brand}
                        </Typography>
                        <Typography component="p">
                            Lorem Ipsum.....
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Link to="/">
                        <Button size="small" color="primary" >
                            <b> Back</b>
                        </Button>
                    </Link>
                    <Button size="small" color="secondary" >
                        <b>Delete this Car</b>
                    </Button>

                </CardActions>
            </Card >
        );
    }
}

export default viewDetail;
