import React, { Component } from "react";
import PropTypes from 'prop-types';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";


class ViewDetail extends Component {

    // initialize our state 
    state = {
        data: [],
        id: null,
        brand: "Ejemplo",
        model: null,
        color: null,
        fuel_type: null,
        engine_volume: null,
        traction: null,
        price: null,
        intervalIsSet: false,
        idToDelete: null,
        idToUpdate: null,
        objectToUpdate: null,
        selected: [],
        newselected: [],
    };

    givemestate(id, brand, model, color, fuel_type, engine_volume, traction, price) {
        console.log("APP en viewDetails: " + id);
        this.setState({ id: id });
        this.setState({ brand: brand });
        this.setState({ model: model });
        this.setState({ color: color });
        this.setState({ fuel_type: fuel_type });
        this.setState({ engine_volume: engine_volume });
        this.setState({ traction: traction });
        this.setState({ price: price });
    }

    createSortHandler = property => event => {
        this.props.onRequestSort(event, property);
    };

    render() {

        this.givemestate(this.props.id,this.props.brand,this.props.model,this.props.color,this.props.fuel_type,this.props.engine_volume,this.props.traction,this.props.price);

        return (
            <Card style={{ padding: "50px", margin: "50px", border: "15px", borderRadius: "20px", textAlign: "center" }}>
                <h1>Detail View</h1>
                <CardActionArea>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                            {this.state.brand}
                        </Typography>
                        <Typography component="p">
                            Lorem Ipsum.....
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Link to="/">
                        <Button size="small" color="primary" >
                            <b> Back</b>
                        </Button>
                    </Link>
                    <Button size="small" color="secondary" >
                        <b>Delete this Car</b>
                    </Button>

                </CardActions>
            </Card >
        );
    }
}

export default ViewDetail;
