// /client/App.js
import React, { Component } from "react";
import axios from "axios";

class App extends Component {
  // initialize our state 
  state = {
    data: [],
    id: null,
    brand: null,
    model: null,
    color: null,
    fuel_type: null,
    engine_volume: null,
    traction: null,
    price: null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null
  };

  cargarDatos(id) {
    console.log("HOLA PIPO");
    console.log(id);
    document.getElementById("item").style.color = "red";
  }

  // when component mounts, first thing it does is fetch all existing data in our db
  // then we incorporate a polling logic so that we can easily see if our db has 
  // changed and implement those changes into our UI
  componentDidMount() {
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  // never let a process live forever 
  // always kill a process everytime we are done using it
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }

  // just a note, here, in the front end, we use the id key of our data object 
  // in order to identify which we want to Update or delete.
  // for our back end, we use the object id assigned by MongoDB to modify 
  // data base entries

  // our first get method that uses our backend api to 
  // fetch data from our data base
  getDataFromDb = () => {
    fetch("http://localhost:3001/api/getData")
      .then(data => data.json())
      .then(res => this.setState({ data: res.data }));
  };

  // our put method that uses our backend api
  // to create new query into our data base
  putDataToDB = (brand, model, color, fuel_type, engine_volume, traction, price) => {
    let currentIds = this.state.data.map(data => data.id);
    let idToBeAdded = 1;
    while (currentIds.includes(idToBeAdded)) {
      ++idToBeAdded;
    }

    axios.post("http://localhost:3001/api/putData", {
      id: idToBeAdded,
      brand: brand,
      model: model,
      color: color,
      fuel_type: fuel_type,
      engine_volume: engine_volume,
      traction: traction,
      price: price
    });
  };


  // our delete method that uses our backend api 
  // to remove existing database information
  deleteFromDB = idTodelete => {
    let objIdToDelete = null;
    this.state.data.forEach(data => {
      if (data._id === idTodelete) {
        objIdToDelete = data._id;
      }
    });
    if (objIdToDelete == null) {
      console.log("NULL NO HAY NADA");
    }
    if (objIdToDelete != null) {
      axios.delete("http://localhost:3001/api/deleteData", {
        data: {
          id: objIdToDelete

        }
      });
    };
  }


  // our update method that uses our backend api
  // to overwrite existing data base information
  updateDB = (idToUpdate, updateToApply) => {
    let objIdToUpdate = null;
    this.state.data.forEach(dat => {
      if (dat.id === idToUpdate) {
        objIdToUpdate = dat._id;
      }
    });
    let brand = this.state.data.map(data => data.brand);
    let model = this.state.data.map(data => data.model);
    let color = this.state.data.map(data => data.color);
    let fuel_type = this.state.data.map(data => data.fuel_type);
    let engine_volume = this.state.data.map(data => data.engine_volume);
    let traction = this.state.data.map(data => data.traction);
    let price = this.state.data.map(data => data.price);

    axios.post("http://localhost:3001/api/updateData", {
      id: objIdToUpdate,
      update: {
        brand: brand,
        model: model,
        color: color,
        fuel_type: fuel_type,
        engine_volume: engine_volume,
        traction: traction,
        price: price
      }
    });
  };


  // here is our UI
  // it is easy to understand their functions when you 
  // see them render into our screen
  render() {
    const { data } = this.state; console.log(data)
    return (
      <div style={{ backgroundColor: "#eceff1" }}>
        <div style={{ textAlign: "-webkit-center" }}>
          <div style={{ padding: "10px" }}>

            <input
              id="brand"
              type="text"
              onChange={e => this.setState({ brand: e.target.value })}
              placeholder="brand"
              style={{ width: "1030px", height: "25px", paddingLeft: "15px", border: "15px" }}
            />
          </div>
          <div style={{ padding: "10px" }}>
            <input
              id="model"
              type="text"
              onChange={e => this.setState({ model: e.target.value })}
              placeholder="model"
              style={{ width: "500px", paddingLeft: "15px", border: "15px", height: "25px" }}
            />
            <input
              id="color"
              type="text"
              onChange={e => this.setState({ color: e.target.value })}
              placeholder="color"
              style={{ width: "500px", paddingLeft: "15px", marginLeft: "10px", height: "25px", border: "15px" }}
            />
          </div>
          <div style={{ padding: "10px" }}>
            <input
              id="fuel_type"
              type="text"
              onChange={e => this.setState({ fuel_type: e.target.value })}
              placeholder="fuel_type"
              style={{ width: "500px", paddingLeft: "15px", border: "15px", height: "25px" }}
            />

            <input
              id="engine_volume"
              type="text"
              onChange={e => this.setState({ engine_volume: e.target.value })}
              placeholder="engine_volume"
              style={{ width: "500px", paddingLeft: "15px", marginLeft: "10px", height: "25px", border: "15px" }}
            />
          </div>
          <div style={{ padding: "10px" }}>
            <input
              id="traction"
              type="text"
              onChange={e => this.setState({ traction: e.target.value })}
              placeholder="traction"
              style={{ width: "500px", paddingLeft: "15px", border: "15px", height: "25px" }}
            />

            <input
              id="price"
              type="text"
              onChange={e => this.setState({ price: e.target.value })}
              placeholder="price"
              style={{ width: "500px", paddingLeft: "15px", marginLeft: "10px", height: "25px", border: "15px" }}
            />
          </div>

          <div style={{ paddingTop: "10px", paddingBottom: "10px" }}>
            <button onClick={() => { this.putDataToDB(this.state.brand, this.state.model, this.state.color, this.state.fuel_type, this.state.engine_volume, this.state.traction, this.state.price) }}
              style={{ width: "150px", backgroundColor: "#ab47bc", color: "#fff", paddingLeft: "15px", marginLeft: "10px", border: "15px", height: "25px" }}>
              <b>ADD</b>
            </button>

            <button onClick={() => { this.updateDB(this.state.idToUpdate, this.state.updateToApply) }}
              style={{ width: "150px", backgroundColor: "#ab47bc", color: "#fff", paddingLeft: "15px", marginLeft: "20px", border: "15px", height: "25px" }}>
              <b>UPDATE</b>
            </button>
          </div>


          <div style={{ padding: "10px" }}>
            <input
              id="idToDelete"
              style={{ width: "500px", paddingLeft: "15px", border: "15px", height: "25px" }}
              type="text"
              onChange={e => this.setState({ idToDelete: e.target.value })}
              placeholder="put id of item to delete here"
            />
            <button onClick={() => this.deleteFromDB(this.state.idToDelete)}
              style={{ width: "150px", backgroundColor: "#ab47bc", color: "#fff", paddingLeft: "15px", marginLeft: "20px", border: "15px", height: "25px" }}>
              <b> DELETE</b>
            </button>
          </div>
        </div>
        <ul style={{ color: "#fff", backgroundColor: "#7b1fa2", padding: "50px" }}>
          <div style={{ color: "#fff", fontFamily: "Verdana" }} ><h1><b>LIST OF CARS -</b> {data.length}</h1></div>
          {data.length <= 0
            ? "NO DB ENTRIES YET"
            : data.map(dat => (
              <ul id="item"
                onClick={() => this.cargarDatos(data)}
                style={{ textAlign: "center", backgroundColor: "#4a148c", borderRadius: "15px", padding: "40px", marginBottom: "15px" }}
                key={data.price}>
                <font size="20"><span style={{ color: "#f8bbd0" }}><b>ID: </b></span> {dat._id}</font> <br />
                <span style={{ color: "#f8bbd0", fontFamily: "Verdana" }}><b> Brand: </b></span> {dat.brand}
                <span style={{ color: "#f8bbd0", paddingLeft: "40px", fontFamily: "Verdana" }}> <b>Model: </b></span> {dat.model}
                <span style={{ color: "#f8bbd0", paddingLeft: "40px", fontFamily: "Verdana" }}><b> Color: </b></span> {dat.color}
                <span style={{ color: "#f8bbd0", paddingLeft: "40px", fontFamily: "Verdana" }}><b> Fuel Type: </b></span> {dat.fuel_type} <br />
                <span style={{ color: "#f8bbd0", fontFamily: "Verdana" }}><b> Engine Volume: </b></span> {dat.engine_volume}
                <span style={{ color: "#f8bbd0", paddingLeft: "40px", fontFamily: "Verdana" }}><b> Traction: </b></span> {dat.traction}
                <span style={{ color: "#f8bbd0", paddingLeft: "40px", fontFamily: "Verdana" }}><b> Price: </b></span> {dat.price} <br />
              </ul>
            ))}
        </ul>
      </div >
    );
  }
}

export default App;
